// src/components/PrivateRoute.js

import React from "react";
import { Route, Navigate } from "react-router-dom"; // Replace Redirect with Navigate
import { useAuth } from "./AuthContext";

const PrivateRoute = ({ component: Component, roles, ...rest }) => {
  const { currentUser } = useAuth();

  // Check if the user is not authenticated
  if (!currentUser) {
    return <Navigate to="/login" replace />;
  }

  // Check if the user's role is not authorized
  if (roles && !roles.includes(currentUser.role)) {
    return <Navigate to="/unauthorized" replace />;
  }

  // Render the component for authorized users
  return (
    <Route
      {...rest}
      element={(props) => <Component {...props} />} // Use the `element` prop instead of `render`
    />
  );
};

export default PrivateRoute;
