import React from 'react';
import { useNavigate } from 'react-router-dom';

const LoginRolesPage = () => {
  const navigate = useNavigate();

  const handleRoleSelection = (role) => {
    // Redirect to the specific login route based on the selected role
    navigate(`/login/${role}`);
  };

  return (
    <div className="container" style={styles.container}>
      <h1 className="section-title" style={styles.title}>
        Choose Your Role
      </h1>

      <div className="cards" style={styles.cardsContainer}>
        {/* Admin Card */}
        <div
          className="card"
          style={styles.card}
          onClick={() => handleRoleSelection('admin')}
        >
          <div className="card-content" style={styles.cardContent}>
            <h2 style={styles.cardHeader}>Administrator</h2>
            <p style={styles.cardDescription}>
              Access the admin dashboard to manage users and monitor the platform.
            </p>
          </div>
        </div>

        {/* Chemist Card */}
        <div
          className="card"
          style={styles.card}
          onClick={() => handleRoleSelection('chimiste')}
        >
          <div className="card-content" style={styles.cardContent}>
            <h2 style={styles.cardHeader}>Chemist</h2>
            <p style={styles.cardDescription}>
              Manage molecules, analyze chemical structures, and run simulations.
            </p>
          </div>
        </div>

        {/* Pharmacist Card */}
        <div
          className="card"
          style={styles.card}
          onClick={() => handleRoleSelection('pharma')}
        >
          <div className="card-content" style={styles.cardContent}>
            <h2 style={styles.cardHeader}>Pharmacist</h2>
            <p style={styles.cardDescription}>
              Collaborate on the development of drugs and analyze pharmacological data.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

// Styles
const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    minHeight: '100vh',
    backgroundColor: '#f0f0f0',
    padding: '20px',
  },
  title: {
    fontSize: '2.5rem',
    fontWeight: 'bold',
    color: '#8a2be2',
    textAlign: 'center',
    marginBottom: '40px',
  },
  cardsContainer: {
    display: 'flex',
    justifyContent: 'space-around',
    gap: '20px',
    flexWrap: 'wrap',
  },
  card: {
    width: '280px',
    height: '350px',
    borderRadius: '10px',
    boxShadow: '0 8px 16px rgba(0, 0, 0, 0.2)',
    backgroundColor: '#fff',
    cursor: 'pointer',
    transition: 'transform 0.3s ease, box-shadow 0.3s ease',
  },
  cardContent: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '100%',
    padding: '20px',
  },
  cardHeader: {
    fontSize: '1.6rem',
    fontWeight: 'bold',
    color: '#ff69b4',
    marginBottom: '15px',
  },
  cardDescription: {
    fontSize: '1rem',
    color: '#333',
    textAlign: 'center',
  },
};

// Hover effect on card
const cardHoverEffect = {
  ':hover': {
    transform: 'scale(1.05)',
    boxShadow: '0 12px 24px rgba(0, 0, 0, 0.3)',
  },
};

export default LoginRolesPage;
