import React, { useState } from 'react';
import { useHistory } from 'react-router-dom'; // Assurez-vous d'avoir react-router-dom installé

const Form = () => {
  const [role, setRole] = useState('admin'); // Valeur par défaut
  const history = useHistory();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Redirection vers le tableau de bord approprié
    if (role === 'admin') {
      history.push('/dashboard-admin');
    } else if (role === 'pharma') {
      history.push('/dashboard-pharma');
    } else if (role === 'chimiste') {
      history.push('/dashboard-chimistes');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Inscription</h2>
      <div>
        <label>Nom d'utilisateur</label>
        <input type="text" required />
      </div>
      <div>
        <label>Email</label>
        <input type="email" required />
      </div>
      <div>
        <label>Mot de passe</label>
        <input type="password" required />
      </div>
      <div>
        <label>Rôle</label>
        <select value={role} onChange={(e) => setRole(e.target.value)} required>
          <option value="admin">Administrateur</option>
          <option value="pharma">Pharmaceutique</option>
          <option value="chimiste">Chimiste</option>
        </select>
      </div>
      <button type="submit">S'inscrire</button>
    </form>
  );
};

export default Form;
