// src/pages/LoginPage.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { login } from "../utils/auth"; // Import authentication function

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(""); // Handles error messages
  const navigate = useNavigate();

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(""); // Clear previous errors

    try {
      const user = await login(email, password);

      // Store user information in localStorage
      localStorage.setItem("user", JSON.stringify(user));
      localStorage.setItem("userRole", user.role); // Store user role

      // Redirect to dashboard
      navigate("/dashboard");
    } catch (err) {
      setError(err.message || "Une erreur est survenue lors de la connexion.");
    }
  };

  return (
    <div style={styles.container}>
      {/* Login Section */}
      <div style={styles.loginBox}>
        <h2 style={styles.title}>
          <span role="img" aria-label="Connexion sÃ©curisÃ©e">ðŸ”’</span> Se Connecter
        </h2>

        {error && <p style={styles.error} id="login-error">{error}</p>}

        <form onSubmit={handleSubmit} style={styles.form}>
          {/* Email Field */}
          <label htmlFor="email" style={styles.label}>
            Email
          </label>
          <input
            id="email"
            type="email"
            placeholder="Entrez votre email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            onFocus={() => setError("")} // Clears error when user types
            style={styles.input}
            required
            aria-describedby="login-error"
          />

          {/* Password Field */}
          <label htmlFor="password" style={styles.label}>
            Mot de passe
          </label>
          <input
            id="password"
            type="password"
            placeholder="Entrez votre mot de passe"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            onFocus={() => setError("")}
            style={styles.input}
            required
            aria-describedby="login-error"
          />

          {/* Submit Button */}
          <button type="submit" style={styles.button}>
            Se connecter
          </button>
        </form>
      </div>
    </div>
  );
};

// Styles
const styles = {
  container: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    backgroundColor: "#f9f9f9",
  },
  loginBox: {
    width: "350px",
    padding: "40px",
    backgroundColor: "#fff",
    borderRadius: "10px",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
  },
  title: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "20px",
    color: "#ff69b4", // Pink theme
    textAlign: "center",
  },
  label: {
    fontSize: "1rem",
    fontWeight: "bold",
    marginBottom: "5px",
    color: "#333",
  },
  error: {
    color: "red",
    marginBottom: "10px",
    textAlign: "center",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "15px",
  },
  input: {
    padding: "10px",
    border: "1px solid #ccc",
    borderRadius: "5px",
    fontSize: "1rem",
    transition: "border-color 0.3s ease",
  },
  inputFocus: {
    borderColor: "#8a2be2", // Purple theme when focused
  },
  button: {
    padding: "12px",
    backgroundColor: "#8a2be2", // Purple theme
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    fontSize: "1rem",
    cursor: "pointer",
    transition: "background-color 0.3s ease",
  },
  buttonHover: {
    backgroundColor: "#6c21a7", // Darker purple on hover
  },
};

// Apply focus effect to inputs
styles.input[":focus"] = styles.inputFocus;

// Apply hover effect to button
styles.button[":hover"] = styles.buttonHover;

export default LoginPage;
