import React from 'react';
import { useNavigate } from 'react-router-dom';
import '././index.css'; // Assure-toi que ce fichier contient les styles ci-dessous

const Roles = () => {
  const navigate = useNavigate();

  const handleCardClick = (role) => {
    switch(role) {
      case 'admin':
        navigate('/login/admin');
        break;
      case 'chimiste':
        navigate('/login/chimiste');
        break;
      case 'pharma':
        navigate('/login/pharma');
        break;
      default:
        navigate('/');
        break;
    }
  };

  return (
    <div className="roles-container">
      <h2 className="roles-title">Choose your Role</h2>
      <div className="roles-card-group">
        <div className="role-card admin-card" onClick={() => handleCardClick('admin')}>
          <h3>Admin</h3>
          <p>Manage users, resources, and data</p>
        </div>

        <div className="role-card chimiste-card" onClick={() => handleCardClick('chimiste')}>
          <h3>Chimiste</h3>
          <p>Submit molecular structures and get predictions</p>
        </div>

        <div className="role-card pharma-card" onClick={() => handleCardClick('pharma')}>
          <h3>Pharma</h3>
          <p>Optimize molecules for clinical trials</p>
        </div>
      </div>
    </div>
  );
};

export default Roles;
