// src/pages/LandingPage.js
import React from "react";
import { Link } from "react-router-dom";

const projectName = "Quantum Drug Design"; // Replace this with your actual project name

const LandingPage = () => {
  return (
    <div style={styles.container}>
      {/* Hero Section */}
      <section style={styles.hero}>
        <h1 style={styles.title}>
          <span role="img" aria-label="Scientific project">üî¨</span> Bienvenue sur {projectName}
        </h1>
        <p style={styles.subtitle}>
          Une plateforme d√©di√©e √† la gestion des mol√©cules, l'analyse clinique et le d√©veloppement scientifique.
        </p>
        <Link to="/login" style={styles.ctaButton} aria-label="Se connecter √† la plateforme">
          Se connecter
        </Link>
      </section>

      {/* Features Section */}
      <section style={styles.featuresSection}>
        <h2 style={styles.sectionTitle}>Nos Fonctionnalit√©s</h2>
        <div style={styles.featureList}>
          {/* Feature: Authentification Multi-R√¥le */}
          <FeatureCard
            title="Authentification Multi-R√¥le"
            description="Acc√®s s√©curis√© pour Admins, Chimistes, Pharmaciens et D√©veloppeurs."
          />

          {/* Feature: Visualisation 3D */}
          <FeatureCard
            title="Visualisation 3D"
            description="Explorez les structures mol√©culaires en 3D avec pr√©cision."
          />

          {/* Feature: Analyse Graphique */}
          <FeatureCard
            title="Analyse Graphique"
            description="Graphiques interactifs pour analyser les donn√©es scientifiques."
          />

          {/* Feature: Monitoring des Mod√®les */}
          <FeatureCard
            title="Monitoring des Mod√®les"
            description="Suivez les performances des mod√®les IA/ML en temps r√©el."
          />
        </div>
      </section>

      {/* Footer */}
      <footer style={styles.footer}>
        <p>&copy; {new Date().getFullYear()} {projectName}. Tous droits r√©serv√©s.</p>
      </footer>
    </div>
  );
};

// Feature Card Component (Reusable)
const FeatureCard = ({ title, description }) => (
  <div style={styles.featureCard}>
    <h3>{title}</h3>
    <p>{description}</p>
  </div>
);

// Styles
const styles = {
  container: {
    fontFamily: "'Open Sans', sans-serif",
    color: "#333",
    textAlign: "center",
    padding: "20px",
  },
  hero: {
    backgroundColor: "#ff69b4", // Rose th√©matique
    color: "#fff",
    padding: "50px 20px",
    marginBottom: "40px",
  },
  title: {
    fontSize: "3rem",
    margin: "0",
  },
  subtitle: {
    fontSize: "1.5rem",
    marginTop: "10px",
  },
  ctaButton: {
    display: "inline-block",
    padding: "12px 24px",
    backgroundColor: "#8a2be2", // Mauve th√©matique
    color: "#fff",
    textDecoration: "none",
    borderRadius: "5px",
    marginTop: "20px",
    fontSize: "1.2rem",
    fontWeight: "bold",
    transition: "background-color 0.3s ease",
  },
  featuresSection: {
    padding: "40px 20px",
    backgroundColor: "#f9f9f9",
  },
  sectionTitle: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "30px",
    color: "#333",
  },
  featureList: {
    display: "flex",
    justifyContent: "center",
    gap: "20px",
    flexWrap: "wrap",
  },
  featureCard: {
    backgroundColor: "#fff",
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    width: "300px",
    transition: "transform 0.3s ease, box-shadow 0.3s ease",
    cursor: "pointer",
    textAlign: "left",
  },
  footer: {
    marginTop: "40px",
    padding: "20px",
    backgroundColor: "#333",
    color: "#fff",
    textAlign: "center",
  },
};

// Apply hover effect to feature cards
styles.featureCard[":hover"] = {
  transform: "translateY(-5px)",
  boxShadow: "0 6px 10px rgba(0, 0, 0, 0.15)",
};

export default LandingPage;
