import React from 'react';
import { useNavigate } from 'react-router-dom';

const Roles = () => {
  const navigate = useNavigate();

  const handleCardClick = (role) => {
    // Navigates to the respective login page based on the role clicked
    switch(role) {
      case 'admin':
        navigate('/login/admin');  // Navigates to the admin login page
        break;
      case 'chimiste':
        navigate('/login/chimiste');  // Navigates to the chimiste login page
        break;
      case 'pharma':
        navigate('/login/pharma');  // Navigates to the pharma login page
        break;
      default:
        navigate('/');  // Redirects to the home page if no valid role is clicked
        break;
    }
  };

  // Inline styles for the cards
  const cardStyles = {
    display: 'flex',
    justifyContent: 'space-around',
    margin: '20px 0',
    padding: '10px',
    borderRadius: '8px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
  };

  const cardStyle = {
    cursor: 'pointer',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.2s',
    color: 'white', // Change text color to white for better contrast
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2 style={{ textAlign: 'center', color: '#7C4DFF' }}>Choose your Role</h2> {/* Lighter purple heading */}
      <div style={cardStyles}>
        <div 
          style={{ ...cardStyle, backgroundColor: '#B19CD9' }} // Light purple card
          onClick={() => handleCardClick('admin')}
          onMouseEnter={e => e.target.style.transform = 'scale(1.05)'}
          onMouseLeave={e => e.target.style.transform = 'scale(1)'}
        >
          <h3>Admin</h3>
          <p>Manage users, resources, and data</p>
        </div>

        <div 
          style={{ ...cardStyle, backgroundColor: '#B3D9F1' }} // Softer light blue card
          onClick={() => handleCardClick('chimiste')}
          onMouseEnter={e => e.target.style.transform = 'scale(1.05)'}
          onMouseLeave={e => e.target.style.transform = 'scale(1)'}
        >
          <h3>Chimiste</h3>
          <p>Submit molecular structures and get predictions</p>
        </div>

        <div 
          style={{ ...cardStyle, backgroundColor: '#A3C9E2' }} // Softer light blue card
          onClick={() => handleCardClick('pharma')}
          onMouseEnter={e => e.target.style.transform = 'scale(1.05)'}
          onMouseLeave={e => e.target.style.transform = 'scale(1)'}
        >
          <h3>Pharma</h3>
          <p>Optimize molecules for clinical trials</p>
        </div>
      </div>
    </div>
  );
};

export default Roles;
