document.addEventListener("DOMContentLoaded", function () {
  // Selecting elements from the DOM
  const signInBtn = document.querySelector("#sign-in-btn");
  const signUpBtn = document.querySelector("#sign-up-btn");
  const container = document.querySelector(".container");
  const signInForm = document.querySelector(".sign-in-form");
  const signUpForm = document.querySelector(".sign-up-form");
  const socialMediaIcons = document.querySelectorAll(".social-icon");

  // Event listener for sign up button
  signUpBtn?.addEventListener("click", () => {
    // Switch to sign-up mode (fade in the signup form)
    container.classList.add("sign-up-mode");
    signUpForm.style.transition = "opacity 0.5s ease-in-out";
    signUpForm.style.opacity = "1";
    signInForm.style.opacity = "0";
  });

  // Event listener for sign in button
  signInBtn?.addEventListener("click", () => {
    // Switch to sign-in mode (fade in the sign-in form)
    container.classList.remove("sign-up-mode");
    signInForm.style.transition = "opacity 0.5s ease-in-out";
    signInForm.style.opacity = "1";
    signUpForm.style.opacity = "0";
  });

  // Event listener for social media icons (like Google login)
  socialMediaIcons?.forEach(icon => {
    icon.addEventListener("click", (e) => {
      // Prevent the default behavior and add some interaction effects
      e.preventDefault();
      icon.style.transform = "scale(1.1)";
      setTimeout(() => {
        icon.style.transform = "scale(1)";
      }, 200);
    });
  });

  // Adding fade-in effect on page load for the forms
  setTimeout(() => {
    signInForm.style.opacity = "1";
    signUpForm.style.opacity = "0";
  }, 300);

  // Adding transitions for containers and forms (smooth effect)
  container.style.transition = "all 1s ease-in-out";
});
