import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LandingPage from './pages/services/LandingPage';
import LoginPageAdmin from './login/LoginPageAdmin';
import LoginPageChimiste from './pages/LoginPageChimiste';
import LoginPagePharma from './pages/LoginPagePharma';
import PrivateRoute from './components/PrivateRoute';
import Dashboard from './pages/Dashboards/Dashboard';
import Roles from './components/Roles';  // Import the Roles component

const App = () => {
  return (
    <Router>
      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<LandingPage />} />
        <Route path="/roles" element={<Roles />} />

        {/* Role-Specific Login Pages */}
        <Route path="/login/admin" element={<LoginPageAdmin />} />
        <Route path="/login/chimiste" element={<LoginPageChimiste />} />
        <Route path="/login/pharma" element={<LoginPagePharma />} />

        {/* Protected Routes */}
        <Route
          path="/dashboard"
          element={
            <PrivateRoute allowedRoles={["admin", "chimiste", "pharma", "dev"]}>
              <Dashboard />
            </PrivateRoute>
          }
        />

        {/* Unauthorized Access Route */}
        <Route path="/unauthorized" element={<h1>Accès non autorisé</h1>} />

        {/* Fallback Route */}
        <Route path="*" element={<h1>Page Non Trouvée</h1>} />
      </Routes>
    </Router>
  );
};

export default App;
