// src/App.js
import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import LandingPage from "./pages/LandingPage";
import LoginPage from "./pages/LoginPage";
import Dashboard from "./pages/Dashboards/Dashboard";
import DashboardChimistes from "./pages/Dashboards/DashboardChimistes";
import DashboardPharma from "./pages/Dashboards/DashboardPharma";
import DashboardDev from "./pages/Dashboards/DashboardDev";
import PrivateRoute from "./components/PrivateRoute"; // Middleware for protected routes
import { ROLES } from "./utils/roles";

const App = () => {
  return (
    <Router>
      <Routes>
        {/* Public Routes: Landing Page */}
        <Route path="/" element={<LandingPage />} />

        {/* Public Route: Login Page */}
        <Route path="/login" element={<LoginPage />} />

        {/* Protected Routes: Dashboards */}
        <Route
          path="/dashboard"
          element={
            <PrivateRoute allowedRoles={[ROLES.ADMIN, ROLES.CHEMIST, ROLES.PHARMACIST, ROLES.DEVELOPER]}>
              <Dashboard />
            </PrivateRoute>
          }
        />

        {/* Chemist Specific Dashboard */}
        <Route
          path="/dashboard/chimiste"
          element={
            <PrivateRoute allowedRoles={[ROLES.CHEMIST]}>
              <DashboardChimistes />
            </PrivateRoute>
          }
        />

        {/* Pharmacist Specific Dashboard */}
        <Route
          path="/dashboard/pharmacist"
          element={
            <PrivateRoute allowedRoles={[ROLES.PHARMACIST]}>
              <DashboardPharma />
            </PrivateRoute>
          }
        />

        {/* Developer Specific Dashboard */}
        <Route
          path="/dashboard/developer"
          element={
            <PrivateRoute allowedRoles={[ROLES.DEVELOPER]}>
              <DashboardDev />
            </PrivateRoute>
          }
        />

        {/* Unauthorized Access Route */}
        <Route path="/unauthorized" element={<h1>Accès non autorisé</h1>} />
      </Routes>
    </Router>
  );
};

export default App;
